import React, { PropsWithChildren, useCallback, useEffect, useMemo, useRef, useState } from 'react';
import {
  Animated,
  Image,
  PanResponder,
  ScrollView,
  Text,
  TouchableOpacity,
  useWindowDimensions,
  View,
} from 'react-native';
import { useSafeAreaInsets } from 'react-native-safe-area-context';

import { BUBBLE_SIZE, ConsoleLogItem, MINUS_SIZE, NetworkLogItem } from './common';
import Network from './network';
import Console from './console';
import System from './system';
import styles from './styles';
import NetworkBadge from './network_badge';
import useNetwork from './hook/useNetwork';
import useConsole from './hook/useConsole';
import AsyncStorage, { isAsyncStorage } from './storage';

const bi =
  'data:image/jpeg;base64,';

interface Props {
  module?: {
    network?: boolean;
    console?: boolean;
  };
  console?: {
    targets: ('log' | 'debug' | 'trace' | 'warn')[];
  };
}

const DevTool = ({ children, module, console }: PropsWithChildren<Props>) => {
  const inset = useSafeAreaInsets();

  const [modules, setModules] = useState([
    {
      key: 'home',
      name: 'Home',
    },
  ]);

  const [httpLogs, setHttpLogs] = React.useState<NetworkLogItem[]>([]);
  useNetwork(module?.network, setHttpLogs);
  useEffect(() => {
    const enabled = module?.network;
    setModules(x => {
      const draft = [...x];
      const ix = draft.findIndex(y => y.key === 'network');
      if (!enabled && ix >= 0) {
        draft.splice(ix, 1);
      } else if (enabled && ix < 0) {
        draft.push({
          key: 'network',
          name: 'Network',
        });
      }
      return draft;
    });
  }, [module?.network]);

  const [consoleLogs, setConsoleLogs] = React.useState<ConsoleLogItem[]>([]);
  useConsole(module?.console, console?.targets ?? ['log', 'debug', 'trace', 'warn'], setConsoleLogs);
  useEffect(() => {
    const enabled = module?.console;
    setModules(x => {
      const draft = [...x];
      const ix = draft.findIndex(y => y.key === 'console');
      if (!enabled && ix >= 0) {
        draft.splice(ix, 1);
      } else if (enabled && ix < 0) {
        draft.push({
          key: 'console',
          name: 'Console',
        });
      }
      return draft;
    });
  }, [module?.console]);

  const enableAS = isAsyncStorage();
  useEffect(() => {
    setModules(x => {
      const draft = [...x];
      const ix = draft.findIndex(y => y.key === 'astorage');
      if (!enableAS && ix >= 0) {
        draft.splice(ix, 1);
      } else if (enableAS && ix < 0) {
        draft.push({
          key: 'astorage',
          name: 'Async Storage',
        });
      }
      return draft;
    });
  }, [enableAS]);

  const counts: { [key: string]: number } = {
    network: httpLogs.length,
    console: consoleLogs.length,
  };

  const { width, height } = useWindowDimensions();
  const [index, setIndex] = useState(0);
  const [left, setLeft] = useState(true);
  const [opened, setOpened] = useState(false);

  const buffer = useRef({ x: MINUS_SIZE, y: inset.top + MINUS_SIZE });
  const anim = useRef(new Animated.ValueXY({ ...buffer.current })).current;
  anim.addListener(value => {
    buffer.current = value;
  });

  const cardAnim = useRef(new Animated.Value(0)).current;
  useEffect(() => {
    if (opened) {
      Animated.timing(cardAnim, {
        toValue: 100,
        duration: 200,
        useNativeDriver: false,
      }).start();
    } else {
      Animated.timing(cardAnim, {
        toValue: 0,
        duration: 150,
        useNativeDriver: false,
      }).start();
    }
  }, [opened]);

  const handleExit = () => {
    Animated.parallel([
      Animated.spring(anim.x, {
        toValue: 0,
        useNativeDriver: false,
      }),
      Animated.spring(anim.y, {
        toValue: 0,
        useNativeDriver: false,
      }),
    ]).start();
    setOpened(false);
  };

  const panResponder = useMemo(() => {
    const handlePress = () => {
      if (!opened) {
        anim.setOffset({ x: buffer.current.x, y: buffer.current.y });
        anim.setValue({ x: 0, y: 0 });

        if (width > 500 && buffer.current.x > 0) {
          Animated.parallel([
            Animated.timing(anim.x, {
              toValue: 20 - buffer.current.x + (width - 420),
              duration: 200,
              useNativeDriver: false,
            }),
            Animated.timing(anim.y, {
              toValue: inset.top + 20 - buffer.current.y,
              duration: 200,
              useNativeDriver: false,
            }),
          ]).start();
        } else {
          Animated.parallel([
            Animated.timing(anim.x, {
              toValue: 20 - buffer.current.x,
              duration: 200,
              useNativeDriver: false,
            }),
            Animated.timing(anim.y, {
              toValue: inset.top + 20 - buffer.current.y,
              duration: 200,
              useNativeDriver: false,
            }),
          ]).start();
        }
      } else {
        setIndex(0);
      }
      setOpened(true);
    };

    return PanResponder.create({
      onStartShouldSetPanResponder: () => true,
      onMoveShouldSetPanResponderCapture: () => true,
      onPanResponderGrant: () => {
        if (opened) return;
        anim.setOffset({ x: buffer.current.x, y: buffer.current.y });
        anim.setValue({ x: 0, y: 0 });
      },
      onPanResponderMove: opened
        ? () => undefined
        : Animated.event(
            [
              null,
              {
                dx: anim.x,
                dy: anim.y,
              },
            ],
            {
              useNativeDriver: false,
            },
          ),
      onPanResponderRelease: (_, gesture) => {
        const { dx, dy, vx, vy } = gesture;
        if ((Math.abs(dx) < 5 && Math.abs(dy) < 5) || opened) {
          handlePress();
          return;
        }

        anim.flattenOffset();

        const { x, y } = buffer.current;
        const x2 = x + width * (0.2 * vx);
        const y2 = y + y * (vy * 0.7);
        setLeft(width / 2 > x2);

        Animated.parallel([
          Animated.spring(anim.x, {
            toValue: width / 2 > x2 ? MINUS_SIZE : width - (BUBBLE_SIZE + MINUS_SIZE),
            useNativeDriver: false,
          }),
          Animated.spring(anim.y, {
            toValue: Math.max(Math.min(y2, height - BUBBLE_SIZE - inset.bottom - MINUS_SIZE), inset.top + MINUS_SIZE),
            useNativeDriver: false,
          }),
        ]).start();
      },
    });
  }, [opened]);

  const cardOpacity = cardAnim.interpolate({
    inputRange: [0, 100],
    outputRange: [0, 1],
  });

  const cardTransformY = cardAnim.interpolate({
    inputRange: [0, 100],
    outputRange: [100, 0],
  });

  const menuLeft = useMemo(() => {
    if (left || width < 500) {
      return 20 + BUBBLE_SIZE / 2;
    }
    return 20 + BUBBLE_SIZE / 2 + (width - 420);
  }, [left, width]);

  const cardLeft = useMemo(() => {
    if (left || width < 500) {
      return 20;
    }
    return width - 400;
  }, [left]);

  const cardRight = useMemo(() => {
    if (left && width > 500) {
      return width - 400;
    }
    return 20;
  }, [left]);

  const renderScreen = useCallback(() => {
    const mod = modules[index];
    if (mod?.key === 'home') {
      return <System />;
    }
    if (mod?.key === 'astorage') {
      return <AsyncStorage />;
    }
    if (mod?.key === 'network') {
      return <Network data={httpLogs} onClear={() => setHttpLogs([])} />;
    }
    if (mod?.key === 'console') {
      return <Console data={consoleLogs} onClear={() => setConsoleLogs([])} />;
    }
    return undefined;
  }, [modules, index, httpLogs, consoleLogs]);

  return (
    <>
      {children}
      <Animated.View {...panResponder.panHandlers} style={[styles.bubble, { transform: anim.getTranslateTransform() }]}>
        <View style={[styles.bubbleItem, opened && index === 0 ? styles.bubbleItemActive : {}]}>
          <Image source={{ uri: bi }} style={{ width: 40, height: 40, borderRadius: 20, marginLeft: 1.5 }} />
          <NetworkBadge data={httpLogs} show={!opened} />
        </View>
      </Animated.View>

      <Animated.View
        pointerEvents={opened ? 'auto' : 'none'}
        style={{
          left: menuLeft,
          opacity: opened ? 1 : 0,
          top: inset.top + 10,

          position: 'absolute',
          right: 0,
          height: BUBBLE_SIZE + 20,
        }}
      >
        <ScrollView
          horizontal
          style={{ flex: 1 }}
          contentContainerStyle={{
            paddingLeft: BUBBLE_SIZE / 2 + 5,
            paddingVertical: 10,
          }}
        >
          {modules?.map((item, n) => {
            if (n === 0) return undefined;
            const desc = counts[item.key];
            return (
              <View style={[styles.bubble, { position: 'relative', marginHorizontal: 5 }]} key={item.key}>
                <TouchableOpacity
                  activeOpacity={1}
                  onPress={() => setIndex(n)}
                  style={[styles.bubbleItem, n === index ? styles.bubbleItemActive : {}]}
                >
                  <Text
                    style={{ fontSize: 10, fontWeight: 'bold', color: '#555', textAlign: 'center' }}
                    allowFontScaling={false}
                  >
                    {item.name}
                  </Text>
                  {desc !== undefined && (
                    <View style={styles.bubbleDesc}>
                      <View style={styles.bubbleDescItem}>
                        <Text style={styles.bubbleDescText}>{desc}</Text>
                      </View>
                    </View>
                  )}
                </TouchableOpacity>
              </View>
            );
          })}

          <View style={[styles.bubble, { position: 'relative', marginHorizontal: 5 }]}>
            <TouchableOpacity activeOpacity={1} onPress={handleExit} style={styles.bubbleItem}>
              <Text style={{ fontSize: 10, fontWeight: 'bold', color: '#555' }} allowFontScaling={false}>
                Exit
              </Text>
            </TouchableOpacity>
          </View>
        </ScrollView>
      </Animated.View>

      <Animated.View
        pointerEvents={opened ? 'auto' : 'none'}
        style={[
          styles.screen,
          {
            left: cardLeft,
            right: cardRight,
            opacity: cardOpacity,
            transform: [{ translateY: cardTransformY }],
            top: inset.top + 20 + BUBBLE_SIZE + 10,
            bottom: inset.bottom + 20,
          },
        ]}
      >
        <View style={{ flex: 1 }}>{renderScreen()}</View>
        <View style={styles.footer}>
          <Text style={styles.footerTitle}>Actbase Tools</Text>
          <Text style={styles.footerUri}>https://actbase.io</Text>
        </View>
      </Animated.View>
    </>
  );
};

export default DevTool;
